    @Test
    fun testBlake2bHash160() {
        for (testVector in blake2b160TestVectors) {
            val result = Blake2b.blake2bHash160(testVector.input.hexToByteArray())
            assertEquals(20, result.size)
            assertEquals(testVector.output, result.toHexString())
        }
    }

    @Test
    fun testBlake2bHash224() {
        for (testVector in blake2b224TestVectors) {
            val result = Blake2b.blake2bHash224(testVector.input.hexToByteArray())
            assertEquals(28, result.size)
            assertEquals(testVector.output, result.toHexString())
        }
    }

    @Test
    fun testBlake2bHash256() {
        for (testVector in blake2b256TestVectors) {
            val result = Blake2b.blake2bHash256(testVector.input.hexToByteArray())
            assertEquals(32, result.size)
            assertEquals(testVector.output, result.toHexString())
        }
    }
